template.value:
// Create Apollo client with normalized cache
private val apolloClient = ApolloClient.Builder()
    .serverUrl("http://localhost:8080/graphql")
    .normalizedCache(MemoryCacheFactory(10_000_000).chain(SqlNormalizedCacheFactory(getDatabaseName(conferenceName))))
    .build()

// Gets list of sessions from backend and then observes the cache for any changes
val sessions = apolloClient.query(GetSessionsQuery()).watch().map {
    it.dataAssertNoErrors.sessions.map { it.sessionDetails }
}

sealed interface SessionsUiState {
    object Loading : SessionsUiState

    data class Success(
        val conferenceName: String,
        val confDates: List<LocalDate>,
        val sessionsByStartTimeList: List<Map<String, List<SessionDetails>>>
    ) : SessionsUiState
}

query GetSessions {
    sessions {
        ...SessionDetails
    }
}


fragment SessionDetails on Session {
    id
    title
    start
    speakers {
        name
    }
}

class ConfettiViewModel: KMMViewModel() {
    private val repository = ConfettiRepository()

    $END$
}

@NativeCoroutinesState
val uiState: StateFlow<SessionsUiState> = repository.sessions.map { sessions ->

    // Group sessions by date
    val sessionsByDateMap = sessions.groupBy { it.start.date }
    val confDates = sessionsByDateMap.keys.toList().sorted()

    // Create list for each date with sessions grouped by start time
    val sessionsByStartTime = sessionsByDateMap.map { (_, sessions) ->
        sessions.groupBy {
            dateTimeFormatter.format(it.start, currentSystemDefault(), "HH:mm")
        }
    }

    SessionsUiState.Success(repository.conferenceName, confDates, sessionsByStartTime)
}.stateIn(viewModelScope, SharingStarted.WhileSubscribed(), SessionsUiState.Loading)

@Composable
fun SessionView(session: SessionDetails) {
    Column(Modifier.padding(horizontal = 16.dp, vertical = 8.dp)) {
        Text(session.title, style = MaterialTheme.typography.bodyLarge)
        Text(session.sessionSpeakerInfo(), style = MaterialTheme.typography.bodyMedium, fontWeight = FontWeight.Bold)
    }
}

@Composable
fun SessionListView(uiState: SessionsUiState.Success) {
    val sessionsMap = uiState.sessionsByStartTimeList[0]
    LazyColumn {
        sessionsMap.forEach { sessions ->
            items(sessions.value) { session ->
                SessionView(session)
            }
        }
    }
}

suspend fun main() {
    val repo = ConfettiRepository()

    repo.sessions.collect { sessions ->
        sessions.forEach { session ->
            println("${session.start}  ${session.title}")
        }
    }
}

@Composable
fun SessionListScreen(viewModel: ConfettiViewModel = getViewModel()) {
    val uiState by viewModel.uiState.collectAsStateWithLifecycle()

    when (val state = uiState) {
        SessionsUiState.Loading ->
            Column(modifier = Modifier.fillMaxSize().wrapContentSize(Alignment.Center)) {
                CircularProgressIndicator()
            }

        is SessionsUiState.Success -> {
            SessionListView(state)
        }
    }
}

class MainActivity : ComponentActivity() {

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)

        setContent {
            ConfettiTheme {
                SessionListScreen()
            }
        }
    }
}

item {
    Column(Modifier.padding(horizontal = 16.dp, vertical = 8.dp)) {
        Text(sessions.key, fontWeight = FontWeight.Bold, color = MaterialTheme.colorScheme.primary)
        Divider()
    }
}
